var time = metadata.ts;

// Initialize the date object with the timestamp in milliseconds (GMT time)
var dateGMT = new Date(parseInt(time));  // This is the GMT time

// Dubai time offset in milliseconds (GMT+4)
var dubaiOffset = 4 * 60 * 60 * 1000; // 4 hours in milliseconds

// Create a new date object adjusted for Dubai time
var dateDubai = new Date(dateGMT.getTime() + dubaiOffset);

// Extract GMT time (UTC)
var yearGMT = dateGMT.getUTCFullYear();
var monthGMT = dateGMT.getUTCMonth() + 1; // getUTCMonth() returns 0-11, so we add 1
var dayGMT = dateGMT.getUTCDate();
var hoursGMT = dateGMT.getUTCHours();
var minutesGMT = dateGMT.getUTCMinutes();
var secondsGMT = dateGMT.getUTCSeconds();
var millisecondsGMT = dateGMT.getUTCMilliseconds();

// Extract Dubai time
var yearDubai = dateDubai.getFullYear();
var monthDubai = dateDubai.getMonth() + 1; // getMonth() returns 0-11, so we add 1
var dayDubai = dateDubai.getDate();
var hoursDubai = dateDubai.getHours();
var minutesDubai = dateDubai.getMinutes();
var secondsDubai = dateDubai.getSeconds();
var millisecondsDubai = dateDubai.getMilliseconds();

// Construct a GMT date string in the format "YYYY-MM-DD"
msg.dateGMT = yearGMT + "-" + (monthGMT < 10 ? "0" + monthGMT : monthGMT) + "-" + (dayGMT < 10 ? "0" + dayGMT : dayGMT);

// Construct a GMT time string (HH:mm:ss.SSS)
msg.timeGMT = (hoursGMT < 10 ? "0" + hoursGMT : hoursGMT) + ":" + 
              (minutesGMT < 10 ? "0" + minutesGMT : minutesGMT) + ":" + 
              (secondsGMT < 10 ? "0" + secondsGMT : secondsGMT) + "." + 
              (millisecondsGMT < 100 ? "0" + (millisecondsGMT < 10 ? "00" + millisecondsGMT : "0" + millisecondsGMT) : millisecondsGMT);

// Construct a Dubai date string in the format "YYYY-MM-DD"
msg.dateDubai_sumpeople = yearDubai + "-" + (monthDubai < 10 ? "0" + monthDubai : monthDubai) + "-" + (dayDubai < 10 ? "0" + dayDubai : dayDubai);

// Construct a Dubai time string (HH:mm:ss.SSS)
msg.timeDubai_sumpeople = (hoursDubai < 10 ? "0" + hoursDubai : hoursDubai) + ":" + 
                (minutesDubai < 10 ? "0" + minutesDubai : minutesDubai) + ":" + 
                (secondsDubai < 10 ? "0" + secondsDubai : secondsDubai)  ;
               // (millisecondsDubai < 100 ? "0" + (millisecondsDubai < 10 ? "00" + millisecondsDubai : "0" + millisecondsDubai) : millisecondsDubai);

// Also returning the original timestamp for reference
msg.time = time;

return { msg: msg, metadata: metadata, msgType: msgType };

