var time = metadata.ts;

// Convert milliseconds to seconds
var secondsSinceEpoch = time / 1000;

// Calculate the number of seconds in a year, month, and day
var secondsInYear = 365 * 24 * 60 * 60; // Approximation (ignoring leap years)
var secondsInMonth = 30 * 24 * 60 * 60; // Approximation (simplification)
var secondsInDay = 24 * 60 * 60;

// Calculate year
var year = 1970;
while (secondsSinceEpoch >= secondsInYear) {
    secondsSinceEpoch -= secondsInYear;
    year++;
}

// Calculate month
var month = 1; // January
while (secondsSinceEpoch >= secondsInMonth) {
    secondsSinceEpoch -= secondsInMonth;
    month++;
}

// Calculate day
var day = 1; // Start from the first day of the month
while (secondsSinceEpoch >= secondsInDay) {
    secondsSinceEpoch -= secondsInDay;
    day++;
}

// Calculate hours, minutes, and seconds using integer division
var hours = (secondsSinceEpoch / 3600);
var minutes = (secondsSinceEpoch % 3600) / 60;
var seconds = secondsSinceEpoch % 60;

// Construct a date string in the format "YYYY-MM-DD"
msg.date = year + "-" + (month < 10 ? "0" + month : month) + "-" + (day < 10 ? "0" + day : day);

// Construct a time string (HH:mm:ss) without float values
msg.timeOnly = (hours < 10 ? "0" + hours : hours) + ":" + (minutes < 10 ? "0" + minutes : minutes) + ":" + (seconds < 10 ? "0" + seconds : seconds);

msg.time = time;

return { msg: msg, metadata: metadata, msgType: msgType };



























