#container {
   display: flex;
    flex-wrap: wrap;  /*Allows wrapping to the next line */
    gap: 10px;  /*Adjust the spacing between the boxes */
    justify-content: center; /* Centers the boxes horizontally */
    padding: 10px; /* Optional: add padding around the container */
}

.tbDatasource-container {

    background-color: #f8f9fa;
    border-radius: 10px;
    box-shadow: 0 0 5px black;
    box-sizing: border-box;
    margin: 10px;
    padding: 15px;
    text-align: center;
}

.tbDatasource-row {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 100%;
}

.tbDatasource-row-label {
    font-size: 1.2rem;
    color: #333;
}

.tbDatasource-row-value {
    font-size: 2rem;
    font-weight: bold;
