descriptor.controllerScript:
self.onInit = function() {
    self.ctx.labelCells = [];
    self.ctx.valueCells = [];
    self.ctx.datasourceContainers = [];

    self.ctx.$container.addClass('container');

    for (var i = 0; i < self.ctx.datasources.length; i++) {
        var tbDatasource = self.ctx.datasources[i];
        var datasourceId = 'tbDatasource' + i;
        self.ctx.$container.append(
            "<div id='" + datasourceId + "' class='tbDatasource-container'></div>"
        );

        var datasourceContainer = $('#' + datasourceId, self.ctx.$container);
        self.ctx.datasourceContainers.push(datasourceContainer);

        for (var a = 0; a < tbDatasource.dataKeys.length; a++) {
            var dataKey = tbDatasource.dataKeys[a];
            var rowId = 'row' + i + '-' + a;
            datasourceContainer.append(
                "<div id='" + rowId + "' class='tbDatasource-row'>" +
                "<div class='tbDatasource-row-label'>" + dataKey.label + "</div>" +
                "<div class='tbDatasource-row-value'></div></div>"
            );
            var row = $('#' + rowId, datasourceContainer);
            var labelCell = $('.tbDatasource-row-label', row);
            self.ctx.labelCells.push(labelCell);
            var valueCell = $('.tbDatasource-row-value', row);
            self.ctx.valueCells.push(valueCell);
        }
    }
    self.onResize();
}

self.onDataUpdated = function() {
    for (var i = 0; i < self.ctx.valueCells.length; i++) {
        var cellData = self.ctx.data[i];
        if (cellData && cellData.data && cellData.data.length > 0) {
            var tvPair = cellData.data[cellData.data.length - 1];
            var value = tvPair[1];
            var txtValue;

            if (isNumber(value)) {
                var decimals = self.ctx.decimals;
                var units = self.ctx.units;
                if (cellData.dataKey.decimals || cellData.dataKey.decimals === 0) {
                    decimals = cellData.dataKey.decimals;
                }
                if (cellData.dataKey.units) {
                    units = cellData.dataKey.units;
                }
                txtValue = self.ctx.utils.formatValue(value, decimals, units, true);
            } else {
                txtValue = value;
            }
            self.ctx.valueCells[i].html(txtValue);

            // Change color based on value
            if (value > 0) {
                self.ctx.valueCells[i].css('color', 'red');
            } else {
                self.ctx.valueCells[i].css('color', 'green');
            }
        }
    }

    function isNumber(n) {
        return !isNaN(parseFloat(n)) && isFinite(n);
    }
}

self.onResize = function() {
    var containerWidth = self.ctx.$container.width();
    var containerHeight = self.ctx.$container.height();
    var datasourceContainersPerRow = 0; // Change to fit your design

   // var containerWidthPercentage = (100 / datasourceContainersPerRow) + '%';

   /* for (var i = 0; i < self.ctx.datasourceContainers.length; i++) {
        self.ctx.datasourceContainers[i].css('width', containerWidthPercentage);
    }*/

    var valueFontSize = Math.min(containerHeight / 9, 18);
    var labelFontSize = Math.min(containerHeight / 12, 14);

    for (i = 0; i < self.ctx.valueCells.length; i++) {
        self.ctx.valueCells[i].css('font-size', valueFontSize + 'px');
        self.ctx.valueCells[i].css('height', valueFontSize * 2.5 + 'px');
        self.ctx.valueCells[i].css('padding', '0px ' + valueFontSize + 'px');
        self.ctx.labelCells[i].css('font-size', labelFontSize + 'px');
        self.ctx.labelCells[i].css('height', labelFontSize * 2.5 + 'px');
        self.ctx.labelCells[i].css('padding', '0px ' + labelFontSize + 'px');
    }
}

self.onDestroy = function() {}

